mapper.select:

SELECT count(*)
FROM businessman
WHERE rest_id = #{restaurantId}


SELECT count(*)
FROM businessman
WHERE bman_license_number = #{licenseNumber}

mapper.update:

UPDATE businessman
SET 
	bman_license_number=#{licenseNumber}, bman_company_name=#{companyName}, 
	bman_account=#{account}
WHERE 
	bman_id=#{businessmanId}
